export async function fetchUsers() {
    try {
        const response = await fetch('https://jsonplaceholder.typicode.com/users');
        if (!response.ok) {
            throw new Error('Failed to fetch users');
        }
        const data = await response.json();
        // Assuming the API response contains user data in an array named 'users'
        return data.users.map((user, index) => ({
            id: user.id,
            name: user.name,
            email: user.email,
            joinedOn: user.joinedOn, // Adjust according to the API response structure
            commentCount: user.commentCount // Adjust according to the API response structure
        }));
    } catch (error) {
        console.error('Error fetching users:', error);
        return [];
    }
}
