export async function fetchUsers() {
    try {
        // Initialize an array to store all users
        let allUsers = [];

        // Define the number of pages to fetch
        const totalPages = 10; // For example, fetch 10 pages

        // Loop through each page and fetch users
        for (let page = 1; page <= totalPages; page++) {
            const response = await fetch(`https://jsonplaceholder.typicode.com/users?_page=${page}`);
            if (!response.ok) {
                throw new Error('Failed to fetch users');
            }
            const users = await response.json();

            // Concatenate users to the array
            allUsers = allUsers.concat(users);
        }

        // Map user objects to the required format
        return allUsers.map((user, index) => ({
            id: user.id,
            name: user.name,
            email: user.email,
            // Assuming 'joinedOn' and 'commentCount' are not provided by the API
            joinedOn: new Date(), // Placeholder for 'joinedOn' field
            commentCount: Math.floor(Math.random() * 101) // Placeholder for 'commentCount' field
        }));
    } catch (error) {
        console.error('Error fetching users:', error);
        return []; // Return an empty array or handle the error accordingly
    }
}
